              </div>

              {exercise.duration && (
                <div className="text-center p-3 bg-gray-50 dark:bg-gray-800 rounded-lg">
                  <p className="text-sm text-gray-600 dark:text-gray-400">Dura√ß√£o</p>
                  <p className="font-semibold">{exercise.duration}</p>
                </div>
              )}
