import React, { useState, useEffect } from 'react';
import { Dialog, DialogContent, DialogHeader, DialogTitle } from './ui/dialog';
import { Button } from './ui/button';
import { Card, CardContent } from './ui/card';
import { Play, Pause, Square, Timer, RotateCcw } from 'lucide-react';
import { Exercise } from '../types';

interface ExerciseTimerProps {
  exercise: Exercise;
  isOpen: boolean;
  onClose: () => void;
  onComplete: () => void;
}

export function ExerciseTimer({ exercise, isOpen, onClose, onComplete }: ExerciseTimerProps) {
  const [currentPhase, setCurrentPhase] = useState<'preparation' | 'exercise' | 'rest' | 'completed'>('preparation');
  const [timeRemaining, setTimeRemaining] = useState(10); // 10 seconds preparation
  const [isRunning, setIsRunning] = useState(false);
  const [currentSet, setCurrentSet] = useState(1);
  const [totalSets, setTotalSets] = useState(3);

  // Parse exercise duration (e.g., "30 seconds", "2 minutes")
  const parseExerciseDuration = (duration?: string): number => {
    if (!duration) return 30;
    const minutes = duration.match(/(\d+)\s*minutes?/);
    const seconds = duration.match(/(\d+)\s*seconds?/);

    if (minutes) return parseInt(minutes[1]) * 60;
    if (seconds) return parseInt(seconds[1]);
    return 30; // default
  };

  // Parse rest duration (e.g., "30 seconds", "1 minute")
  const parseRestDuration = (rest: string): number => {
    const minutes = rest.match(/(\d+)\s*minutes?/);
    const seconds = rest.match(/(\d+)\s*seconds?/);

    if (minutes) return parseInt(minutes[1]) * 60;
    if (seconds) return parseInt(seconds[1]);
    return 30; // default
  };

  const exerciseDuration = parseExerciseDuration(exercise.duration);
  const restDuration = parseRestDuration(exercise.rest);

  useEffect(() => {
    let interval: NodeJS.Timeout;

    if (isRunning && timeRemaining > 0) {
      interval = setInterval(() => {
        setTimeRemaining(time => time - 1);
      }, 1000);
    } else if (timeRemaining === 0) {
      handlePhaseComplete();
    }

    return () => clearInterval(interval);
  }, [isRunning, timeRemaining]);

  const handlePhaseComplete = () => {
    setIsRunning(false);

    switch (currentPhase) {
      case 'preparation':
        setCurrentPhase('exercise');
        setTimeRemaining(exerciseDuration);
        setIsRunning(true);
        break;
      case 'exercise':
        if (currentSet < totalSets) {
          setCurrentPhase('rest');
          setTimeRemaining(restDuration);
          setIsRunning(true);
        } else {
          setCurrentPhase('completed');
          onComplete();
        }
        break;
      case 'rest':
        setCurrentSet(prev => prev + 1);
        setCurrentPhase('exercise');
        setTimeRemaining(exerciseDuration);
        setIsRunning(true);
        break;
    }
  };

  const handleStart = () => {
    setIsRunning(true);
    console.log('Starting exercise:', exercise.id);
  };

  const handlePause = () => {
    setIsRunning(false);
  };

  const handleReset = () => {
    setIsRunning(false);
    setCurrentPhase('preparation');
    setTimeRemaining(10);
    setCurrentSet(1);
  };

  const handleStop = () => {
    setIsRunning(false);
    onClose();
  };

  const formatTime = (seconds: number): string => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins.toString().padStart(2, '0')}:${secs.toString().padStart(2, '0')}`;
  };

  const getPhaseTitle = () => {
    switch (currentPhase) {
      case 'preparation': return 'Get Ready!';
      case 'exercise': return `Set ${currentSet} of ${totalSets}`;
      case 'rest': return 'Rest Time';
      case 'completed': return 'Excellent Work!';
      default: return '';
    }
  };

  const getPhaseColor = () => {
    switch (currentPhase) {
      case 'preparation': return 'bg-blue-100 text-blue-800 dark:bg-blue-800 dark:text-blue-100';
      case 'exercise': return 'bg-green-100 text-green-800 dark:bg-green-800 dark:text-green-100';
      case 'rest': return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-800 dark:text-yellow-100';
      case 'completed': return 'bg-purple-100 text-purple-800 dark:bg-purple-800 dark:text-purple-100';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-100';
    }
  };

  const getPhaseInstruction = () => {
    switch (currentPhase) {
      case 'preparation': return 'Prepare yourself for the exercise. Make sure you have enough space and are ready to begin.';
      case 'exercise': return exercise.description || 'Perform the exercise with proper form. Focus on your breathing and technique.';
      case 'rest': return 'Take a breather! Stay hydrated and prepare for the next set.';
      case 'completed': return 'You completed the exercise! Great job on your workout session.';
      default: return '';
    }
  };

  return (
    <Dialog open={isOpen} onOpenChange={onClose}>
      <DialogContent className="sm:max-w-[500px]">
        <DialogHeader>
          <DialogTitle className="text-xl font-bold text-center">
            {exercise.name}
          </DialogTitle>
        </DialogHeader>

        <div className="space-y-6">
          {/* Phase Indicator */}
          <div className={`text-center p-4 rounded-lg ${getPhaseColor()}`}>
            <h3 className="text-lg font-semibold mb-2">{getPhaseTitle()}</h3>
            <p className="text-sm">{getPhaseInstruction()}</p>
          </div>

          {/* Timer Display */}
          <Card>
            <CardContent className="p-6">
              <div className="text-center">
                <div className="text-6xl font-bold text-primary mb-2">
                  {formatTime(timeRemaining)}
                </div>
                <div className="text-sm text-gray-600 dark:text-gray-400">
                  {currentPhase === 'preparation' && 'Preparation Time'}
                  {currentPhase === 'exercise' && 'Exercise Time'}
                  {currentPhase === 'rest' && 'Rest Time'}
                  {currentPhase === 'completed' && 'Completed!'}
                </div>
              </div>
            </CardContent>
          </Card>

          {/* Exercise Info */}
          <div className="grid grid-cols-2 gap-4 text-sm">
            <div className="text-center">
              <div className="font-semibold text-gray-700 dark:text-gray-300">Duration</div>
              <div className="text-gray-600 dark:text-gray-400">{exercise.duration || '30 seconds'}</div>
            </div>
            <div className="text-center">
              <div className="font-semibold text-gray-700 dark:text-gray-300">Rest</div>
              <div className="text-gray-600 dark:text-gray-400">{exercise.rest}</div>
            </div>
          </div>

          {/* Controls */}
          <div className="flex justify-center space-x-3">
            {!isRunning && currentPhase !== 'completed' && (
              <Button onClick={handleStart} className="bg-green-600 hover:bg-green-700">
                <Play className="w-4 h-4 mr-2" />
                Start
              </Button>
            )}

            {isRunning && (
              <Button onClick={handlePause} variant="outline">
                <Pause className="w-4 h-4 mr-2" />
                Pause
              </Button>
            )}

            <Button onClick={handleReset} variant="outline">
              <RotateCcw className="w-4 h-4 mr-2" />
              Reset
            </Button>

            <Button onClick={handleStop} variant="destructive">
              <Square className="w-4 h-4 mr-2" />
              Stop
            </Button>
          </div>

          {currentPhase === 'completed' && (
            <div className="text-center space-y-3">
              <p className="text-green-600 dark:text-green-400 font-semibold">
                üéâ Exercise completed! You're building stronger habits.
              </p>
              <Button onClick={onClose} className="bg-primary hover:bg-primary/90">
                Finish Workout
              </Button>
            </div>
          )}
        </div>
      </DialogContent>
    </Dialog>
  );
}

client/src/components/FloatingActionButt.content:
import React, { useState } from "react";
import { Button } from "./ui/button";
import {
  Card,
  CardContent,
} from "./ui/card";
import { Input } from "./ui/input";
import { Label } from "./ui/label";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "./ui/select";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
} from "./ui/dialog";
import {
  Plus,
  Droplets,
  Weight,
  Heart,
  X,
} from "lucide-react";
import { useApp } from "../contexts/AppContext";

export function FloatingActionButton() {
  const { logWeight, logMood, logWater } = useApp();
  const [isOpen, setIsOpen] = useState(false);
  const [showWeightDialog, setShowWeightDialog] = useState(false);
  const [showMoodDialog, setShowMoodDialog] = useState(false);
  const [showWaterDialog, setShowWaterDialog] = useState(false);

  // Form states
  const [weightInput, setWeightInput] = useState("");
  const [moodInput, setMoodInput] = useState("");
  const [waterInput, setWaterInput] = useState("");

  const handleLogWeight = () => {
    const weight = parseFloat(weightInput);
    if (weight > 0) {
      logWeight(weight);
      setWeightInput("");
      setShowWeightDialog(false);
      setIsOpen(false);
    }
  };

  const handleLogMood = () => {
    const mood = parseInt(moodInput);
    if (mood >= 1 && mood <= 5) {
      logMood(mood);
      setMoodInput("");
      setShowMoodDialog(false);
      setIsOpen(false);
    }
  };

  const handleLogWater = () => {
    const water = parseFloat(waterInput);
    if (water > 0) {
      logWater(water);
      setWaterInput("");
      setShowWaterDialog(false);
      setIsOpen(false);
    }
  };

  const getMoodEmoji = (mood: string) => {
    const moodMap: { [key: string]: string } = {
      "1": "üò¢",
      "2": "üòî",
      "3": "üòê",
      "4": "üòä",
      "5": "üòÑ",
    };
    return moodMap[mood] || "üòê";
  };

  return (
    <>
      {/* Floating Action Button Container */}
      <div className="fixed bottom-20 right-4 z-50 flex flex-col items-end">
        {/* Quick Action Options - positioned above the main button */}
        {isOpen && (
          <div className="flex flex-col items-end space-y-2 mb-4">
            <Button
              onClick={() => setShowWaterDialog(true)}
              className="bg-blue-500 hover:bg-blue-600 text-white shadow-lg w-auto px-4"
              size="sm"
            >
              <Droplets className="w-4 h-4 mr-2" />
              Log Water
            </Button>
            <Button
              onClick={() => setShowWeightDialog(true)}
              className="bg-purple-500 hover:bg-purple-600 text-white shadow-lg w-auto px-4"
              size="sm"
            >
              <Weight className="w-4 h-4 mr-2" />
              Log Weight
            </Button>
            <Button
              onClick={() => setShowMoodDialog(true)}
              className="bg-pink-500 hover:bg-pink-600 text-white shadow-lg w-auto px-4"
              size="sm"
            >
              <Heart className="w-4 h-4 mr-2" />
              Log Mood
            </Button>
          </div>
        )}

        {/* Main FAB - always stays in the same position */}
        <Button
          onClick={() => setIsOpen(!isOpen)}
          className="h-14 w-14 rounded-full bg-primary hover:bg-primary/90 text-white shadow-lg transition-transform hover:scale-105 flex-shrink-0"
          size="sm"
        >
          {isOpen ? (
            <X className="w-6 h-6" />
          ) : (
            <Plus className="w-6 h-6" />
          )}
        </Button>
      </div>

      {/* Weight Dialog */}
      <Dialog open={showWeightDialog} onOpenChange={setShowWeightDialog}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <Weight className="w-5 h-5" />
              Log Weight
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="weight-input">Weight (kg)</Label>
              <Input
                id="weight-input"
                type="number"
                step="0.1"
                value={weightInput}
                onChange={(e) => setWeightInput(e.target.value)}
                placeholder="Enter your weight in kg"
              />
            </div>

            <div className="flex gap-2">
              <Button variant="outline" onClick={() => setShowWeightDialog(false)} className="flex-1">
                Cancel
              </Button>
              <Button onClick={handleLogWeight} disabled={!weightInput} className="flex-1">
                Log Weight
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* Mood Dialog */}
      <Dialog open={showMoodDialog} onOpenChange={setShowMoodDialog}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <Heart className="w-5 h-5" />
              Log Mood
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="mood-select">How are you feeling?</Label>
              <Select value={moodInput} onValueChange={setMoodInput}>
                <SelectTrigger>
                  <SelectValue placeholder="Select your mood">
                    {moodInput && (
                      <span className="flex items-center gap-2">
                        {getMoodEmoji(moodInput)}
                        {moodInput === "1" && "Very Sad"}
                        {moodInput === "2" && "Sad"}
                        {moodInput === "3" && "Neutral"}
                        {moodInput === "4" && "Happy"}
                        {moodInput === "5" && "Very Happy"}
                      </span>
                    )}
                  </SelectValue>
                </SelectTrigger>
                <SelectContent>
                  <SelectItem value="1">üò¢ Very Sad</SelectItem>
                  <SelectItem value="2">üòî Sad</SelectItem>
                  <SelectItem value="3">üòê Neutral</SelectItem>
                  <SelectItem value="4">üòä Happy</SelectItem>
                  <SelectItem value="5">üòÑ Very Happy</SelectItem>
                </SelectContent>
              </Select>
            </div>

            <div className="flex gap-2">
              <Button variant="outline" onClick={() => setShowMoodDialog(false)} className="flex-1">
                Cancel
              </Button>
              <Button onClick={handleLogMood} disabled={!moodInput} className="flex-1">
                Log Mood
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>

      {/* Water Dialog */}
      <Dialog open={showWaterDialog} onOpenChange={setShowWaterDialog}>
        <DialogContent className="sm:max-w-md">
          <DialogHeader>
            <DialogTitle className="flex items-center gap-2">
              <Droplets className="w-5 h-5" />
              Log Water Intake
            </DialogTitle>
          </DialogHeader>

          <div className="space-y-4">
            <div className="space-y-2">
              <Label htmlFor="water-input">Amount (liters)</Label>
              <Input
                id="water-input"
                type="number"
                step="0.1"
                value={waterInput}
                onChange={(e) => setWaterInput(e.target.value)}
                placeholder="e.g., 0.5"
              />
            </div>

            {/* Quick Amount Buttons */}
            <div className="grid grid-cols-4 gap-2">
              <Button
                variant="outline"
                size="sm"
                onClick={() => setWaterInput("0.25")}
              >
                250ml
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setWaterInput("0.5")}
              >
                500ml
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setWaterInput("0.75")}
              >
                750ml
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={() => setWaterInput("1")}
              >
                1L
              </Button>
            </div>

            <div className="flex gap-2">
              <Button variant="outline" onClick={() => setShowWaterDialog(false)} className="flex-1">
                Cancel
              </Button>
              <Button onClick={handleLogWater} disabled={!waterInput} className="flex-1">
                Log Water
              </Button>
            </div>
          </div>
        </DialogContent>
      </Dialog>
    </>
  );
}

client/src/components/Journal.tsx.content:
import React, { useState, useMemo } from "react";
import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
} from "./ui/card";
import { Input } from "./ui/input";
import { Label } from "./ui/label";
import { Badge } from "./ui/badge";
import { Separator } from "./ui/separator";
import {
  Calendar,
  Droplets,
  Target,
  Heart,
  Utensils,
  ChevronLeft,
  ChevronRight,
  Weight,
  Trash2,
} from "lucide-react";
import { Button } from "./ui/button";
import { useTranslation } from "../hooks/useTranslation";
import { useApp } from "../contexts/AppContext";

export function Journal() {
  const { t } = useTranslation();
  const { state, dispatch } = useApp();
  const [selectedDate, setSelectedDate] = useState(
    new Date().toISOString().split("T")[0]
  );

  // Helper functions to navigate dates
  const goToPreviousDay = () => {
    const currentDate = new Date(selectedDate);
    currentDate.setDate(currentDate.getDate() - 1);
    setSelectedDate(currentDate.toISOString().split("T")[0]);
  };

  const goToNextDay = () => {
    const currentDate = new Date(selectedDate);
    const tomorrow = new Date();
    tomorrow.setDate(tomorrow.getDate() + 1);

    if (currentDate < tomorrow) {
      currentDate.setDate(currentDate.getDate() + 1);
      setSelectedDate(currentDate.toISOString().split("T")[0]);
    }
  };

  // Function to delete a meal log
  const deleteMealLog = (mealLogId: string) => {
    const updatedMealLogs = state.userData.mealLogs?.filter(
      log => log.id !== mealLogId
    ) || [];

    dispatch({
      type: "SET_USER_DATA",
      payload: {
        ...state.userData,
        mealLogs: updatedMealLogs,
      },
    });
  };

  // Get data for selected date
  const dayData = useMemo(() => {
    const waterLogs = state.userData.waterLog?.filter(
      (log) => log.date === selectedDate
    ) || [];
    const mealLogs = state.userData.mealLogs?.filter(
      (log) => log.date === selectedDate
    ) || [];
    const moodLogs = state.userData.moodLogs?.filter(
      (log) => log.date === selectedDate
    ) || [];
    const weightLogs = state.userData.weights?.filter(
      (log) => log.date === selectedDate
    ) || [];

    const totalWater = waterLogs.reduce((sum, log) => sum + log.liters, 0);
    const totalCalories = mealLogs.reduce((sum, log) => sum + log.calories, 0);
    const totalProtein = mealLogs.reduce((sum, log) => sum + log.protein, 0);
    const totalCarbs = mealLogs.reduce((sum, log) => sum + log.carbs, 0);
    const totalFat = mealLogs.reduce((sum, log) => sum + log.fat, 0);
    const lastMood = moodLogs.length > 0 ? moodLogs[moodLogs.length - 1] : null;
    const dayWeight = weightLogs.length > 0 ? weightLogs[weightLogs.length - 1] : null;

    return {
      water: totalWater,
      calories: totalCalories,
      protein: totalProtein,
      carbs: totalCarbs,
      fat: totalFat,
      mood: lastMood,
      weight: dayWeight,
      mealLogs,
      waterLogs,
    };
  }, [selectedDate, state.userData]);

  const formatDate = (dateStr: string) => {
    const date = new Date(dateStr);
    return date.toLocaleDateString("en-US", {
      weekday: "long",
      year: "numeric",
      month: "long",
      day: "numeric",
    });
  };

  const getMoodEmoji = (mood: number) => {
    const moodMap = {
      1: "üò¢",
      2: "üòî",
      3: "üòê",
      4: "üòä",
      5: "üòÑ",
    };
    return moodMap[mood as keyof typeof moodMap] || "üòê";
  };

  const getMoodLabel = (mood: number) => {
    const moodLabels = {
      1: "Very Sad",
      2: "Sad",
      3: "Neutral",
      4: "Happy",
      5: "Very Happy",
    };
    return moodLabels[mood as keyof typeof moodLabels] || "Unknown";
  };

  const isToday = selectedDate === new Date().toISOString().split("T")[0];
  const isFuture = new Date(selectedDate) > new Date();

  return (
    <div className="space-y-6">
      {/* Date Navigation */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center justify-between">
            <div className="flex items-center gap-2">
              <Calendar className="w-5 h-5" />
              Daily Journal
            </div>
            <div className="flex items-center gap-2">
              <Button
                variant="outline"
                size="sm"
                onClick={goToPreviousDay}
              >
                <ChevronLeft className="w-4 h-4" />
              </Button>
              <Button
                variant="outline"
                size="sm"
                onClick={goToNextDay}
                disabled={isFuture}
              >
                <ChevronRight className="w-4 h-4" />
              </Button>
            </div>
          </CardTitle>
        </CardHeader>
        <CardContent>
          <div className="space-y-4">
            <div>
              <Label htmlFor="journal-date">Select Date</Label>
              <Input
                id="journal-date"
                type="date"
                value={selectedDate}
                onChange={(e) => setSelectedDate(e.target.value)}
                max={new Date().toISOString().split("T")[0]}
              />
            </div>
            <div className="text-center">
              <h2 className="text-xl font-semibold">
                {formatDate(selectedDate)}
              </h2>
              {isToday && (
                <Badge variant="secondary" className="mt-1">
                  Today
                </Badge>
              )}
            </div>
          </div>
        </CardContent>
      </Card>

      {/* Daily Summary */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
        {/* Water Intake */}
        <Card>
          <CardHeader className="pb-3">
            <CardTitle className="text-base flex items-center gap-2">
              <Droplets className="w-4 h-4 text-blue-500" />
              Water Intake
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-center">
              <div className="text-2xl font-bold text-blue-600">
                {dayData.water.toFixed(1)}L
              </div>
              <div className="text-sm text-muted-foreground">
                {dayData.waterLogs.length} log{dayData.waterLogs.length !== 1 ? 's' : ''}
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Calories */}
        <Card>
          <CardHeader className="pb-3">
            <CardTitle className="text-base flex items-center gap-2">
              <Target className="w-4 h-4 text-orange-500" />
              Calories
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-center">
              <div className="text-2xl font-bold text-orange-600">
                {dayData.calories}
              </div>
              <div className="text-sm text-muted-foreground">
                {dayData.mealLogs.length} meal{dayData.mealLogs.length !== 1 ? 's' : ''}
              </div>
            </div>
          </CardContent>
        </Card>

        {/* Mood */}
        <Card>
          <CardHeader className="pb-3">
            <CardTitle className="text-base flex items-center gap-2">
              <Heart className="w-4 h-4 text-pink-500" />
              Mood
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-center">
              {dayData.mood ? (
                <>
                  <div className="text-3xl mb-1">
                    {getMoodEmoji(dayData.mood.mood)}
                  </div>
                  <div className="text-sm font-medium">
                    {getMoodLabel(dayData.mood.mood)}
                  </div>
                  <div className="text-xs text-muted-foreground">
                    {dayData.mood.time}
                  </div>
                </>
              ) : (
                <div className="text-muted-foreground">
                  <div className="text-2xl">üòê</div>
                  <div className="text-sm">No mood logged</div>
                </div>
              )}
            </div>
          </CardContent>
        </Card>

        {/* Weight */}
        <Card>
          <CardHeader className="pb-3">
            <CardTitle className="text-base flex items-center gap-2">
              <Weight className="w-4 h-4 text-purple-500" />
              Weight
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="text-center">
              {dayData.weight ? (
                <>
                  <div className="text-2xl font-bold text-purple-600">
                    {dayData.weight.weight} kg
                  </div>
                  <div className="text-xs text-muted-foreground">
                    {dayData.weight.time}
                  </div>
                </>
              ) : (
                <div className="text-muted-foreground">
                  <div className="text-2xl">-</div>
                  <div className="text-sm">No weight logged</div>
                </div>
              )}
            </div>
          </CardContent>
        </Card>
      </div>

      {/* Macronutrients Summary */}
      {dayData.mealLogs.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Target className="w-5 h-5" />
              Daily Macronutrients
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-3 gap-4">
              <div className="text-center p-4 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
                <div className="text-2xl font-bold text-blue-600">
                  {dayData.protein.toFixed(1)}g
                </div>
                <div className="text-sm text-muted-foreground">Protein</div>
              </div>
              <div className="text-center p-4 bg-green-50 dark:bg-green-900/20 rounded-lg">
                <div className="text-2xl font-bold text-green-600">
                  {dayData.carbs.toFixed(1)}g
                </div>
                <div className="text-sm text-muted-foreground">Carbs</div>
              </div>
              <div className="text-center p-4 bg-purple-50 dark:bg-purple-900/20 rounded-lg">
                <div className="text-2xl font-bold text-purple-600">
                  {dayData.fat.toFixed(1)}g
                </div>
                <div className="text-sm text-muted-foreground">Fat</div>
              </div>
            </div>
          </CardContent>
        </Card>
      )}

      {/* Water Logs */}
      {dayData.waterLogs.length > 0 && (
        <Card>
          <CardHeader>
            <CardTitle className="flex items-center gap-2">
              <Droplets className="w-5 h-5" />
              Water Intake Logs
            </CardTitle>
          </CardHeader>
          <CardContent>
            <div className="space-y-3">
              {dayData.waterLogs.map((waterLog, index) => (
                <div key={waterLog.id || index} className="flex items-center justify-between p-3 bg-blue-50 dark:bg-blue-900/20 rounded-lg">
                  <div className="flex-1">
                    <div className="font-medium text-blue-700 dark:text-blue-300">
                      {waterLog.liters}L water
                    </div>
                    <div className="text-sm text-muted-foreground">
                      {waterLog.time}
                    </div>
                  </div>
                  <div className="text-right">
                    <div className="font-semibold text-blue-600">
                      {(waterLog.liters * 1000).toFixed(0)}ml
                    </div>
                  </div>
                </div>
              ))}
            </div>
          </CardContent>
        </Card>
      )}

      {/* Detailed Logs */}
      {dayData.mealLogs.length > 0 && (
        <Card>
          <CardHeader>
