import { useState, useEffect } from "react";

type TranslationKey = string;
type TranslationParams = Record<string, string | number>;

interface TranslationData {
  [key: string]: any;
}

const translations: Record<string, TranslationData> = {};

type SupportedLanguage = "en-US" | "fr-FR" | "es-ES";

export function useTranslation() {
  const [currentLanguage, setCurrentLanguage] =
    useState<SupportedLanguage>("en-US");
  const [isLoading, setIsLoading] = useState(true);

  const getLocalizedText = (value: string | Record<string, string>) => {
    if (typeof value === "string") return value;
    return value[currentLanguage] || Object.values(value)[0] || "";
  };

  useEffect(() => {
    const loadTranslations = async () => {
      try {
        const languages: SupportedLanguage[] = [
          "en-US",
          "fr-FR",
          "es-ES",
        ];

        for (const lang of languages) {
          if (!translations[lang]) {
            const module = await import(`../data/translations/${lang}.json`);
            translations[lang] = module.default;
          }
        }

        setIsLoading(false);
      } catch (error) {
        console.error("Failed to load translations:", error);
        setIsLoading(false);
      }
    };

    loadTranslations();
  }, []);

  useEffect(() => {
    const savedLanguage = localStorage.getItem(
      "wellness_tracker_language",
    ) as SupportedLanguage;
    if (
      savedLanguage &&
      ["en-US", "fr-FR", "es-ES"].includes(savedLanguage)
    ) {
      setCurrentLanguage(savedLanguage);
    }
  }, []);

  const changeLanguage = (language: string) => {
    const supportedLang = language as SupportedLanguage;
    setCurrentLanguage(supportedLang);
    localStorage.setItem("wellness_tracker_language", supportedLang);
  };

  const t = (key: TranslationKey, params?: TranslationParams): string => {
    if (isLoading) return key;

    if (!key || typeof key !== "string") {
      return "";
    }
    const keys = (key || "").split(".");
    let value = translations[currentLanguage];

    for (const k of keys) {
      if (value && typeof value === "object" && k in value) {
        value = value[k];
      } else {
        // Fallback to en-US
        if (currentLanguage !== "en-US" && translations["en-US"]) {
          let fallbackValue = translations["en-US"];
          for (const fallbackK of keys) {
            if (
              fallbackValue &&
              typeof fallbackValue === "object" &&
              fallbackK in fallbackValue
            ) {
              fallbackValue = fallbackValue[fallbackK];
            } else {
              return key;
            }
          }
          value = fallbackValue;
          break;
        }
        return key;
      }
    }

    // Handle multilingual objects
    if (typeof value === "object" && value !== null) {
      // If it's a multilingual object, try to get the current language
      if (value[currentLanguage]) {
        value = value[currentLanguage];
      } else if (value["en-NG"]) {
        value = value["en-NG"];
      } else {
        // Get first available value
        const firstKey = Object.keys(value)[0];
        if (firstKey) {
          value = value[firstKey];
        } else {
          console.warn(
            `Translation key "${key}" resolved to non-string value:`,
            value,
          );
          return key;
        }
      }
    }

    if (typeof value !== "string") {
      console.warn(
        `Translation key "${key}" resolved to non-string value:`,
        value,
      );
      return key;
    }

    if (params) {
      return value.replace(/\{\{(\w+)\}\}/g, (match, paramKey) => {
        return params[paramKey]?.toString() || match;
      });
    }

    return value;
  };

  const getRandomQuote = (): string => {
    if (isLoading) return "";

    const quotes =
      translations[currentLanguage]?.cultural?.motivationalQuotes || [];
    if (quotes.length === 0) return "";

    const randomIndex = Math.floor(Math.random() * quotes.length);
    return quotes[randomIndex] || "";
  };

  return {
    t,
    currentLanguage,
    changeLanguage,
    isLoading,
    getRandomQuote,
    getLocalizedText, // ‚úÖ Adicionado aqui
  };
}

