  const getCategoryColor = (category: string) => {
    switch (category) {
      case 'milestone': return 'bg-purple-100 text-purple-800 dark:bg-purple-800 dark:text-purple-100';
      case 'consistency': return 'bg-blue-100 text-blue-800 dark:bg-blue-800 dark:text-blue-100';
      case 'challenge': return 'bg-yellow-100 text-yellow-800 dark:bg-yellow-800 dark:text-yellow-100';
      case 'activity': return 'bg-green-100 text-green-800 dark:bg-green-800 dark:text-green-100';
      case 'nutrition': return 'bg-orange-100 text-orange-800 dark:bg-orange-800 dark:text-orange-100';
      default: return 'bg-gray-100 text-gray-800 dark:bg-gray-800 dark:text-gray-100';
    }
  };

  const BadgeStats = () => (
    <div className="grid grid-cols-1 md:grid-cols-3 gap-4 mb-8">
      <Card className="bg-gradient-to-r from-green-500 to-green-600 text-white border-0">
        <CardContent className="p-6 text-center">
          <div className="flex items-center justify-center mb-2">
            <Trophy className="w-8 h-8 mr-2" />
            <div className="text-3xl font-bold">{unlockedBadges.length}</div>
          </div>
          <div className="text-green-100 font-medium">{t('badges.unlocked')}</div>
        </CardContent>
      </Card>

      <Card className="bg-gradient-to-r from-gray-400 to-gray-500 text-white border-0">
        <CardContent className="p-6 text-center">
          <div className="flex items-center justify-center mb-2">
            <Lock className="w-8 h-8 mr-2" />
            <div className="text-3xl font-bold">{lockedBadges.length}</div>
          </div>
          <div className="text-gray-100 font-medium">{t('badges.locked')}</div>
        </CardContent>
      </Card>

      <Card className="bg-gradient-to-r from-blue-500 to-blue-600 text-white border-0">
        <CardContent className="p-6 text-center">
          <div className="flex items-center justify-center mb-2">
            <Award className="w-8 h-8 mr-2" />
            <div className="text-3xl font-bold">{allBadges.length}</div>
          </div>
          <div className="text-blue-100 font-medium">Total</div>
        </CardContent>
      </Card>
    </div>
  );

  return (
    <div className="min-h-screen bg-gray-50 dark:bg-gray-900 pb-20">
      {/* Header */}
      <header className="bg-white dark:bg-gray-800 shadow-sm">
        <div className="px-4 py-6">
          <div className="flex items-center justify-between mb-4">
            <div>
              <h1 className="text-2xl font-poppins font-bold text-gray-800 dark:text-gray-100">
                {t('badges.title')}
              </h1>
              <p className="text-gray-600 dark:text-gray-400 text-sm">
                {t('badges.subtitle')}
              </p>
            </div>
            <div className="text-right">
              <div className="text-2xl font-bold text-primary">{Math.round((unlockedBadges.length / allBadges.length) * 100)}%</div>
              <div className="text-xs text-gray-500">{t('progress.title')}</div>
            </div>
          </div>

          {/* Search Bar */}
          <div className="relative">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 w-5 h-5" />
            <Input
              type="text"
              placeholder={`${t('common.search') || 'Buscar'} badges...`}
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="pl-10 rounded-2xl"
            />
          </div>
        </div>
      </header>

      <div className="px-4 py-6">
        <BadgeStats />

        <Tabs defaultValue="all" className="w-full">
          <TabsList className="grid w-full grid-cols-3 mb-6">
            <TabsTrigger value="all" className="flex items-center space-x-2">
              <Award className="w-4 h-4" />
              <span>{t('meals.all')} ({allBadges.length})</span>
            </TabsTrigger>
            <TabsTrigger value="unlocked" className="flex items-center space-x-2">
              <Trophy className="w-4 h-4" />
              <span>{t('badges.unlocked')} ({unlockedBadges.length})</span>
            </TabsTrigger>
            <TabsTrigger value="locked" className="flex items-center space-x-2">
              <Lock className="w-4 h-4" />
              <span>{t('badges.locked')} ({lockedBadges.length})</span>
            </TabsTrigger>
          </TabsList>

          <TabsContent value="all" className="mt-6">
            {/* Category Filter */}
            <div className="flex flex-wrap gap-2 mb-6">
              {categories.map((category) => (
                <button
                  key={category}
                  onClick={() => setSelectedCategory(category)}
                  className={`flex items-center space-x-2 px-4 py-2 rounded-full text-sm font-medium transition-all duration-200 ${
                    selectedCategory === category
                      ? 'bg-primary text-white shadow-lg transform scale-105'
                      : 'bg-white dark:bg-gray-800 text-gray-700 dark:text-gray-300 hover:bg-gray-100 dark:hover:bg-gray-700 shadow-sm'
                  }`}
                >
                  {category !== 'all' && getCategoryIcon(category)}
                  <span>
                    {category === 'all' ? t('meals.all') : 
                     category === 'milestone' ? t('badges.categories.milestone') :
                     category === 'consistency' ? t('badges.categories.consistency') :
                     category === 'challenge' ? t('challenges.title') :
                     category === 'activity' ? t('badges.categories.activity') :
                     category === 'nutrition' ? t('badges.categories.nutrition') : category}
                  </span>
                  <Badge variant="secondary" className="ml-1">
                    {category === 'all' ? allBadges.length : allBadges.filter(b => b.category === category).length}
                  </Badge>
                </button>
              ))}
            </div>

            {filteredBadges.length > 0 ? (
              <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                {filteredBadges.map((badge) => (
                  <div key={badge.id} className="relative group">
                    <div className={`absolute -top-2 -right-2 z-10 ${getCategoryColor(badge.category)} px-2 py-1 rounded-full text-xs flex items-center space-x-1 transition-opacity ${badge.isUnlocked ? 'opacity-100' : 'opacity-0 group-hover:opacity-100'}`}>
                      {getCategoryIcon(badge.category)}
                    </div>
                    <BadgeCard badge={badge} showDate={true} />
                    {!badge.isUnlocked && (
                      <div className="mt-3 p-3 bg-gray-50 dark:bg-gray-800 rounded-lg">
                        <p className="text-xs text-gray-600 dark:text-gray-400 text-center leading-relaxed">
                          {badge.requirement}
                        </p>
                      </div>
                    )}
                  </div>
                ))}
              </div>
            ) : (
              <Card className="p-12 text-center border-dashed border-2 border-gray-300 dark:border-gray-600">
                <Search className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-600 dark:text-gray-400 text-lg mb-2">
                  {t('badges.noBadgesFound')}
                </p>
                <p className="text-gray-500 dark:text-gray-500 text-sm">
                  {t('badges.adjustFilters')}
                </p>
              </Card>
            )}
          </TabsContent>

          <TabsContent value="unlocked" className="mt-6">
            {unlockedBadges.length > 0 ? (
              <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                {unlockedBadges.map((badge) => (
                  <div key={badge.id} className="relative">
                    <div className={`absolute -top-2 -right-2 z-10 ${getCategoryColor(badge.category)} px-2 py-1 rounded-full text-xs flex items-center space-x-1`}>
                      {getCategoryIcon(badge.category)}
                    </div>
                    <BadgeCard badge={badge} showDate={true} />
                  </div>
                ))}
              </div>
            ) : (
              <Card className="p-12 text-center">
                <Trophy className="w-16 h-16 text-gray-400 mx-auto mb-4" />
                <p className="text-gray-600 dark:text-gray-400 text-lg mb-2">
                  {t('badges.noBadgesEarned')}
                </p>
                <p className="text-gray-500 dark:text-gray-500 text-sm">
                  {t('badges.completeActivities')}
                </p>
              </Card>
            )}
          </TabsContent>

          <TabsContent value="locked" className="mt-6">
            {lockedBadges.length > 0 ? (
              <div className="grid grid-cols-2 md:grid-cols-3 lg:grid-cols-4 gap-6">
                {lockedBadges.map((badge) => (
                  <div key={badge.id} className="relative group">
                    <div className={`absolute -top-2 -right-2 z-10 ${getCategoryColor(badge.category)} px-2 py-1 rounded-full text-xs flex items-center space-x-1 opacity-0 group-hover:opacity-100 transition-opacity`}>
                      {getCategoryIcon(badge.category)}
                    </div>
                    <BadgeCard badge={badge} />
                    <div className="mt-3 p-3 bg-gray-50 dark:bg-gray-800 rounded-lg">
                      <p className="text-xs text-gray-600 dark:text-gray-400 text-center leading-relaxed">
                        {badge.requirement}
                      </p>
                    </div>
                  </div>
                ))}
              </div>
            ) : (
              <Card className="p-12 text-center">
                <Trophy className="w-16 h-16 text-yellow-500 mx-auto mb-4" />
                <p className="text-gray-600 dark:text-gray-400 text-lg mb-2">
                  {t('badges.congratulations')}
                </p>
                <p className="text-gray-500 dark:text-gray-500 text-sm">
                  {t('badges.keepUpHabits')}
                </p>
              </Card>
            )}
          </TabsContent>
        </Tabs>
      </div>
    </div>
  );
}

client/src/pages/challenges.tsx.content:
import React, { useState, useEffect } from 'react';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { Button } from '../components/ui/button';
import { Badge } from '../components/ui/badge';
import { Tabs, TabsContent, TabsList, TabsTrigger } from '../components/ui/tabs';
import { Progress } from '../components/ui/progress';
import { Play, CheckCircle, Calendar, Target, Award, Trophy, Sparkles } from 'lucide-react';
import { useTranslation } from '../hooks/useTranslation';
import { useApp } from '../contexts/AppContext';
import { ChallengeCard } from '../components/ChallengeCard';
import challengesData from '../data/challenges.json';

export default function Challenges() {
  const { t } = useTranslation();
  const { state, startChallenge, restartChallenge } = useApp();
  const [selectedTab, setSelectedTab] = useState('available');
  // All challenges are now accessible

  // Helper function to ensure translation returns a string, not an object
  // If object is received, get the first valid string or convert to JSON string to avoid React error
  const safeTranslate = (key: string) => {
    const result = t(key);
    if (typeof result === 'string') return result;
    if (typeof result === 'object' && result !== null) {
      // If object, try to get the first key value
      const firstKey = Object.keys(result)[0];
      if (firstKey && typeof result[firstKey] === 'string') return result[firstKey];
      // fallback to convert object to string
      return JSON.stringify(result);
    }
    return '';
  };

  // Helper function to get translated text from multilingual objects
  const getTranslatedText = (textObj: { [key: string]: string } | string, fallback?: string): string => {
    if (typeof textObj === 'string') return textObj;
    if (typeof textObj === 'object' && textObj !== null) {
      // Try current language first
      const currentLang = state.currentLanguage;
      if (textObj[currentLang]) return textObj[currentLang];
      // Fallback to en-US
      if (textObj['en-US']) return textObj['en-US'];
      // Fallback to first available language
      const firstKey = Object.keys(textObj)[0];
      if (firstKey && textObj[firstKey]) return textObj[firstKey];
    }
    return fallback || '';
  };

  // All challenges are accessible - no need to check access

  const { userData } = state;
  const activeChallenges = Object.values(userData.challenges).filter(c => c.isActive);
  const completedChallenges = Object.values(userData.challenges).filter(c => c.completedDays.length === c.days);
  const availableChallenges = challengesData.filter(c => !userData.challenges[c.id] || !userData.challenges[c.id].isActive);

  // No plan restrictions needed

  const handleStartChallenge = (challengeId: string) => {
    // All challenges are accessible
    startChallenge(challengeId);
  };

  const handleRestartChallenge = (challengeId: string) => {
    restartChallenge(challengeId);
  };

  const ChallengeStats = () => (
    <div className="grid grid-cols-3 gap-4 mb-8">
      <Card className="bg-gradient-to-br from-blue-500/10 to-blue-600/20 border-blue-200 dark:border-blue-800">
        <CardContent className="p-6 text-center">
          <div className="flex items-center justify-center w-12 h-12 bg-blue-500/20 rounded-full mx-auto mb-3">
            <Play className="w-6 h-6 text-blue-600 dark:text-blue-400" />
          </div>
          <div className="text-3xl font-bold text-blue-600 dark:text-blue-400 mb-1">{activeChallenges.length}</div>
          <div className="text-sm text-gray-600 dark:text-gray-400 font-medium">{t('challenges.activeChallengesLabel')}</div>
        </CardContent>
      </Card>

      <Card className="bg-gradient-to-br from-green-500/10 to-green-600/20 border-green-200 dark:border-green-800">
        <CardContent className="p-6 text-center">
          <div className="flex items-center justify-center w-12 h-12 bg-green-500/20 rounded-full mx-auto mb-3">
            <Trophy className="w-6 h-6 text-green-600 dark:text-green-400" />
          </div>
          <div className="text-3xl font-bold text-green-600 dark:text-green-400 mb-1">{completedChallenges.length}</div>
          <div className="text-sm text-gray-600 dark:text-gray-400 font-medium">{t('challenges.completedChallengesLabel')}</div>
        </CardContent>
      </Card>

      <Card className="bg-gradient-to-br from-purple-500/10 to-purple-600/20 border-purple-200 dark:border-purple-800">
        <CardContent className="p-6 text-center">
          <div className="flex items-center justify-center w-12 h-12 bg-purple-500/20 rounded-full mx-auto mb-3">
            <Sparkles className="w-6 h-6 text-purple-600 dark:text-purple-400" />
          </div>
          <div className="text-3xl font-bold text-purple-600 dark:text-purple-400 mb-1">{availableChallenges.length}</div>
          <div className="text-sm text-gray-600 dark:text-gray-400 font-medium">{t('challenges.availableChallengesLabel')}</div>
        </CardContent>
      </Card>
    </div>
  );

  const AvailableChallengeCard = ({ challenge }: { challenge: any }) => {
    return (
      <Card className="overflow-hidden transition-all duration-300 hover:shadow-xl hover:scale-[1.02]">
        <div className="h-2 bg-gradient-to-r from-blue-400 to-purple-500" />

        <CardHeader className="pb-4">
          <div className="flex items-start justify-between">
            <div className="flex-1">
              <div className="flex items-center gap-3 mb-2">
                <CardTitle className="text-xl font-bold text-gray-800 dark:text-gray-100">
                  {typeof challenge.name === 'string' ? challenge.name : safeTranslate(challenge.name)}
                </CardTitle>
              </div>

              <div className="flex items-center gap-4 text-sm text-gray-600 dark:text-gray-400">
                <div className="flex items-center gap-1">
                  <Calendar className="w-4 h-4" />
                  <span>{challenge.days} {t('challenges.daysLabel')}</span>
                </div>
                <div className="flex items-center gap-1">
                  <Target className="w-4 h-4" />
                  <span>{t('challenges.completeChallengeLabel')}</span>
                </div>
              </div>
            </div>

            <div className="w-16 h-16 rounded-2xl flex items-center justify-center bg-gradient-to-br from-blue-500 to-purple-600">
              <Target className="w-8 h-8 text-white" />
            </div>
          </div>
        </CardHeader>

        <CardContent className="pt-0">
          <p className="text-gray-600 dark:text-gray-400 mb-6 leading-relaxed">
            {typeof challenge.description === 'string' ? challenge.description : safeTranslate(challenge.description)}
          </p>

          <div className="space-y-3 mb-6">
            {challenge.dailyTasks && challenge.dailyTasks.slice(0, 3).map((task: any, idx: number) => (
              <div key={idx} className="flex items-center gap-3 p-3 bg-gray-50 dark:bg-gray-800 rounded-lg">
                <div className="w-2 h-2 bg-blue-500 rounded-full" />
                <span className="text-sm text-gray-700 dark:text-gray-300">
                  {typeof task.tasks[0] === 'string' ? task.tasks[0] : safeTranslate(task.tasks[0])}
                </span>
              </div>
            ))}
          </div>

          <Button
            className="w-full"
            onClick={() => handleStartChallenge(challenge.id)}
          >
            {t('challenges.startChallenge')}
          </Button>
        </CardContent>
      </Card>
    );
  };

  const ActiveChallengeCard = ({ challenge }: { challenge: any }) => {
    const progress = (challenge.completedDays.length / challenge.days) * 100;
    const { state, completeTask, uncompleteTask } = useApp();
    const { currentLanguage } = useTranslation();

    // Get today's tasks for the current day
    const currentDay = challenge.currentDay || 1;
    const todayTasks = challenge.dailyTasks && challenge.dailyTasks.find((task: any) => task.day === currentDay);
    // Use the same language that was used when the challenge was created to maintain index consistency
    const challengeLanguage = challenge.language || 'en-US'; 
    const tasks = todayTasks ? (todayTasks.tasks[challengeLanguage] || todayTasks.tasks['en-US'] || []) : [];

    return (
      <Card className="overflow-hidden hover:shadow-xl transition-all duration-300">
        <div className="h-2 bg-gradient-to-r from-green-400 to-teal-500" />

        <CardHeader className="pb-4">
          <CardTitle className="text-xl font-bold text-gray-800 dark:text-gray-100 mb-2">
            {getTranslatedText(challenge.name)}
          </CardTitle>

          <p className="text-gray-600 dark:text-gray-400 mb-4 leading-relaxed">
            {getTranslatedText(challenge.description)}
          </p>

          <Progress value={progress} className="mb-4" />

          <div className="flex justify-between text-sm text-gray-600 dark:text-gray-400">
            <span>{challenge.completedDays.length} / {challenge.days} {t('challenges.daysCompleted')}</span>
            <span>{Math.round(progress)}%</span>
          </div>
        </CardHeader>

        <CardContent className="pt-0">
          {/* Today's Tasks Checklist */}
          <div className="mb-6">
            <h4 className="font-semibold text-gray-800 dark:text-gray-100 mb-3">
              {t('challenges.todaysTasks')} - {t('challenges.day')} {currentDay}
            </h4>
            <div className="space-y-3">
              {tasks.map((task: string, taskIndex: number) => {
                const isCompleted = todayTasks?.completed && todayTasks.completed[taskIndex];
                return (
                  <div key={taskIndex} className="flex items-start gap-3 p-3 bg-gray-50 dark:bg-gray-800 rounded-lg">
                    <button
                      onClick={() => {
                        if (isCompleted) {
                          uncompleteTask(challenge.id, currentDay, taskIndex);
                        } else {
                          completeTask(challenge.id, currentDay, taskIndex);
                        }
                      }}
                      className={`flex-shrink-0 w-5 h-5 rounded border-2 flex items-center justify-center transition-colors ${
                        isCompleted
                          ? 'bg-green-500 border-green-500 text-white'
                          : 'border-gray-300 dark:border-gray-600 hover:border-green-400'
                      }`}
                      data-testid={`task-checkbox-${taskIndex}`}
                    >
                      {isCompleted && <CheckCircle className="w-3 h-3" />}
                    </button>
                    <span className={`text-sm ${
                      isCompleted 
                        ? 'text-gray-500 dark:text-gray-400 line-through' 
                        : 'text-gray-700 dark:text-gray-300'
                    }`}>
                      {task}
                    </span>
                  </div>
                );
              })}
            </div>
          </div>

          <Button 
            className="w-full" 
            onClick={() => handleRestartChallenge(challenge.id)}
            data-testid="restart-challenge-button"
          >
            {t('challenges.restartChallenge')}
          </Button>
        </CardContent>
      </Card>
    );
  };

  return (
    <div className="max-w-5xl mx-auto px-6 py-12">
      <h1 className="text-4xl font-extrabold mb-8">{t('challenges.title')}</h1>

      <ChallengeStats />

      <Tabs value={selectedTab} onValueChange={setSelectedTab} className="mb-8">
        <TabsList>
          <TabsTrigger value="available">{t('challenges.available')}</TabsTrigger>
          <TabsTrigger value="active">{t('challenges.active')}</TabsTrigger>
          <TabsTrigger value="completed">{t('challenges.completed')}</TabsTrigger>
        </TabsList>

        <TabsContent value="available" className="space-y-6">
          {availableChallenges.length === 0 && (
            <p className="text-center text-gray-600 dark:text-gray-400">{t('challenges.noAvailable')}</p>
          )}

          {availableChallenges.map(challenge => (
            <AvailableChallengeCard key={challenge.id} challenge={challenge} />
          ))}
        </TabsContent>

        <TabsContent value="active" className="space-y-6">
          {activeChallenges.length === 0 && (
            <p className="text-center text-gray-600 dark:text-gray-400">{t('challenges.noActive')}</p>
          )}

          {activeChallenges.map(challenge => (
            <ActiveChallengeCard key={challenge.id} challenge={challenge} />
          ))}
        </TabsContent>

        <TabsContent value="completed" className="space-y-6">
          {completedChallenges.length === 0 && (
            <p className="text-center text-gray-600 dark:text-gray-400">{safeTranslate('noCompletedChallenges') || 'Nenhum desafio conclu√≠do ainda.'}</p>
          )}

          {completedChallenges.map(challenge => (
            <Card key={challenge.id} className="overflow-hidden hover:shadow-xl transition-all duration-300">
              <CardHeader>
                <CardTitle>
                  {getTranslatedText(challenge.name)}
                </CardTitle>
              </CardHeader>
              <CardContent>
                <p>{getTranslatedText(challenge.description)}</p>
                <Badge variant="secondary" className="mt-2">
                  {safeTranslate('completedBadge') || 'Conclu√≠do'}
                </Badge>
              </CardContent>
            </Card>
          ))}
        </TabsContent>
      </Tabs>

      {/* Plan system removed - no upgrade popups needed */}
    </div>
  );
}

client/src/pages/course-details.tsx.content:
import React from 'react';
import { useApp } from '../contexts/AppContext';
import { Button } from '../components/ui/button';
import { Card, CardContent } from '../components/ui/card';
import { Accordion, AccordionContent, AccordionItem, AccordionTrigger } from '../components/ui/accordion';
import { ArrowLeft, Play, CheckCircle2, Clock, Video, FileText } from 'lucide-react';
import coursesData from '../data/courses.json';
import { Course, CourseProgress } from '../types';
import { RouteComponentProps } from 'wouter';
import { useTranslation } from '../hooks/useTranslation';

const courses = coursesData as Course[];

interface CourseDetailsProps extends RouteComponentProps {
  params: { id: string };
}

export default function CourseDetails({ params }: CourseDetailsProps) {
  const { t, currentLanguage } = useTranslation();
  const { user, updateUser } = useApp();

  const course = courses.find(c => c.id === params.id);
  const courseProgress = user?.courseProgress?.[params.id];

  if (!course) {
    return (
      <div className="p-4 text-center">
        <h1 className="text-2xl font-bold mb-4">Course Not Found</h1>
        <Button onClick={() => window.history.back()}>
          <ArrowLeft className="w-4 h-4 mr-2" />
          Go Back
        </Button>
      </div>
    );
  }

  const getCourseName = () => {
    if (typeof course.name === 'string') {
      return course.name;
    }
    return course.name[currentLanguage] || course.name['en-NG'] || 'Course';
  };

  const getCourseDescription = () => {
    if (typeof course.description === 'string') {
      return course.description;
    }
    return course.description[currentLanguage] || course.description['en-NG'] || '';
  };

  const getLessonTitle = (lesson: any) => {
    if (typeof lesson.title === 'string') {
      return lesson.title;
    }
    return lesson.title[currentLanguage] || lesson.title['en-NG'] || 'Lesson';
  };

  const getLessonDescription = (lesson: any) => {
    if (typeof lesson.description === 'string') {
      return lesson.description;
    }
    return lesson.description?.[currentLanguage] || lesson.description?.['en-NG'] || '';
  };

  const startCourse = () => {
    if (!user) return;

    const newProgress: CourseProgress = {
      currentLessonId: course.lessons[0].id,
      completedLessons: [],
      startedAt: new Date().toISOString(),
      lastAccessedAt: new Date().toISOString()
    };

    updateUser({
      ...user,
      courseProgress: {
        ...user.courseProgress,
        [course.id]: newProgress
      }
    });
  };

  const continueCourse = () => {
    if (!user || !courseProgress) return;

    updateUser({
      ...user,
      courseProgress: {
        ...user.courseProgress,
        [course.id]: {
          ...courseProgress,
          lastAccessedAt: new Date().toISOString()
        }
      }
    });
  };

  const completedLessons = courseProgress?.completedLessons?.length || 0;
  const progressPercentage = (completedLessons / course.lessons.length) * 100;

  return (
    <div className="p-4 space-y-6">
      <div className="flex items-center gap-4 mb-6">
        <Button variant="ghost" size="sm" onClick={() => window.history.back()}>
          <ArrowLeft className="w-4 h-4" />
        </Button>
        <h1 className="text-2xl font-bold">{getCourseName()}</h1>
      </div>

      <Card>
        <CardContent className="p-6">
          <div className="space-y-4">
            <div className="flex items-center justify-between">
              <div className="flex items-center gap-2">
                <Clock className="w-4 h-4 text-muted-foreground" />
                <span className="text-sm text-muted-foreground">
                  {course.lessons.length} {t('courses.lessons')}
                </span>
              </div>
              <div className="text-sm text-muted-foreground">
                {t('courses.progress')}: {Math.round(progressPercentage)}%
              </div>
            </div>

            <p className="text-muted-foreground">
              {getCourseDescription()}
            </p>

            <div className="flex gap-2">
              {!courseProgress ? (
                <Button onClick={startCourse} className="flex-1">
                  <Play className="w-4 h-4 mr-2" />
                  {t('courses.startCourse')}
                </Button>
              ) : (
                <Button onClick={continueCourse} className="flex-1">
                  <Play className="w-4 h-4 mr-2" />
                  {t('courses.continueCourse')}
                </Button>
              )}
            </div>
          </div>
        </CardContent>
      </Card>

      <Card>
        <CardContent className="p-6">
          <h2 className="text-lg font-semibold mb-4">Course Content</h2>
          <Accordion type="single" collapsible>
            {course.lessons.map((lesson, index) => {
              const isCompleted = courseProgress?.completedLessons?.includes(lesson.id);
              const isCurrent = courseProgress?.currentLessonId === lesson.id;

              return (
                <AccordionItem key={lesson.id} value={lesson.id}>
                  <AccordionTrigger className="text-left">
                    <div className="flex items-center gap-3">
                      {isCompleted ? (
                        <CheckCircle2 className="w-5 h-5 text-green-500" />
                      ) : isCurrent ? (
                        <Play className="w-5 h-5 text-blue-500" />
                      ) : (
                        <div className="w-5 h-5 rounded-full border-2 border-muted-foreground" />
                      )}
                      <div>
                        <div className="font-medium">
                          {index + 1}. {getLessonTitle(lesson)}
                        </div>
                        <div className="text-sm text-muted-foreground flex items-center gap-2">
                          {lesson.type === 'video' ? (
                            <Video className="w-3 h-3" />
                          ) : (
                            <FileText className="w-3 h-3" />
                          )}
                          {lesson.duration}
                        </div>
                      </div>
                    </div>
                  </AccordionTrigger>
                  <AccordionContent>
                    <div className="pl-8 space-y-2">
                      <p className="text-sm text-muted-foreground">
                        {getLessonDescription(lesson)}
                      </p>
                      <Button size="sm" variant="outline">
                        Start Lesson
                      </Button>
                    </div>
                  </AccordionContent>
                </AccordionItem>
              );
            })}
          </Accordion>
        </CardContent>
      </Card>
    </div>
  );
}

