client/src/pages/lesson.tsx.content:
import React from 'react';
import { useApp } from '../contexts/AppContext';
import { Button } from '../components/ui/button';
import { Card, CardContent, CardHeader, CardTitle } from '../components/ui/card';
import { ArrowLeft, ArrowRight, CheckCircle2, Clock, Video, FileText } from 'lucide-react';
import coursesData from '../data/courses.json';
import { Course, CourseProgress } from '../types';
import { RouteComponentProps } from 'wouter';
import { useTranslation } from '../hooks/useTranslation';

const courses = coursesData as Course[];

interface LessonPageProps {
  courseId: string;
  moduleId: string;
  lessonId: string;
}

export default function LessonPage({ courseId, moduleId, lessonId }: LessonPageProps) {
  const { state, dispatch } = useApp();
  const { t } = useTranslation();

  const course = courses.find(c => c.id === courseId);
  const module = course?.modules.find(m => m.id === moduleId);
  const lesson = module?.lessons.find(l => l.id === lessonId);
  const progress = state.userData.courseProgress.find(p => p.courseId === courseId);

  if (!course || !module || !lesson) {
    return (
      <div className="p-4 text-center">
        <h1 className="text-2xl font-bold mb-4">{t('Lesson not found')}</h1>
        <Button onClick={() => window.location.hash = '#/courses'}>
          {t('Back to Courses')}
        </Button>
      </div>
    );
  }

  const isCompleted = progress?.completedLessons.includes(lessonId) || false;

  const markAsCompleted = () => {
    if (isCompleted) return;

    // Update course progress
    let updatedCourseProgress;
    if (progress) {
      const updatedProgress = {
        ...progress,
        completedLessons: [...progress.completedLessons, lessonId],
        lastAccessedAt: new Date().toISOString()
      };
      updatedCourseProgress = state.userData.courseProgress.map(p => 
        p.courseId === courseId ? updatedProgress : p
      );
    } else {
      // Create new progress if it doesn't exist
      const newProgress = {
        courseId,
        completedLessons: [lessonId],
        currentModule: moduleId,
        currentLesson: lessonId,
        startedAt: new Date().toISOString(),
        lastAccessedAt: new Date().toISOString()
      };
      updatedCourseProgress = [...state.userData.courseProgress, newProgress];
    }

    // Update lesson progress
    const existingLessonProgress = state.userData.lessonProgress || [];
    const existingLesson = existingLessonProgress.find(
      lp => lp.lessonId === lessonId && lp.courseId === courseId
    );

    let updatedLessonProgress;
    if (existingLesson) {
      updatedLessonProgress = existingLessonProgress.map(lp =>
        lp.lessonId === lessonId && lp.courseId === courseId
          ? { ...lp, completed: true, completedAt: new Date().toISOString() }
          : lp
      );
    } else {
      const newLessonProgress = {
        lessonId,
        courseId,
        moduleId,
        completed: true,
        completedAt: new Date().toISOString()
      };
      updatedLessonProgress = [...existingLessonProgress, newLessonProgress];
    }

    // Save to localStorage and state
    const updatedUserData = {
      ...state.userData,
      courseProgress: updatedCourseProgress,
      lessonProgress: updatedLessonProgress
    };

    localStorage.setItem('naijaresetUserData', JSON.stringify(updatedUserData));

    dispatch({
      type: 'SET_USER_DATA',
      payload: updatedUserData
    });
  };

  const getNextLesson = () => {
    const allLessons: Array<{ moduleId: string; lessonId: string; lesson: any }> = [];

    course.modules.forEach(mod => {
      mod.lessons.forEach(les => {
        allLessons.push({ moduleId: mod.id, lessonId: les.id, lesson: les });
      });
    });

    const currentIndex = allLessons.findIndex(l => l.lessonId === lessonId);
    return currentIndex < allLessons.length - 1 ? allLessons[currentIndex + 1] : null;
  };

  const getPrevLesson = () => {
    const allLessons: Array<{ moduleId: string; lessonId: string; lesson: any }> = [];

    course.modules.forEach(mod => {
      mod.lessons.forEach(les => {
        allLessons.push({ moduleId: mod.id, lessonId: les.id, lesson: les });
      });
    });

    const currentIndex = allLessons.findIndex(l => l.lessonId === lessonId);
    return currentIndex > 0 ? allLessons[currentIndex - 1] : null;
  };

  const nextLesson = getNextLesson();
  const prevLesson = getPrevLesson();

  const goToNextLesson = () => {
    if (nextLesson) {
      // Update current lesson in progress
      if (progress) {
        const updatedProgress = {
          ...progress,
          currentModule: nextLesson.moduleId,
          currentLesson: nextLesson.lessonId,
          lastAccessedAt: new Date().toISOString()
        };

        const updatedCourseProgress = state.userData.courseProgress.map(p => 
          p.courseId === courseId ? updatedProgress : p
        );

        dispatch({
          type: 'SET_USER_DATA',
          payload: {
            ...state.userData,
            courseProgress: updatedCourseProgress
          }
        });
      }

      window.location.hash = `#/lesson/${courseId}/${nextLesson.moduleId}/${nextLesson.lessonId}`;
    }
  };

  const goToPrevLesson = () => {
    if (prevLesson) {
      window.location.hash = `#/lesson/${courseId}/${prevLesson.moduleId}/${prevLesson.lessonId}`;
    }
  };

  return (
    <div className="p-4 space-y-6">
      <Button
        variant="ghost"
        onClick={() => window.location.hash = `#/course/${courseId}`}
        className="mb-4"
      >
        <ArrowLeft className="w-4 h-4 mr-2" />
        {t('Back to Course')}
      </Button>

      <Card>
        <CardHeader>
          <div className="flex items-center gap-2 text-sm text-muted-foreground mb-2">
            <span>{course.title}</span>
            <span>‚Ä¢</span>
            <span>{module.title}</span>
          </div>

          <CardTitle className="flex items-center gap-3">
            {lesson.type === 'video' ? (
              <Video className="w-5 h-5 text-blue-500" />
            ) : (
              <FileText className="w-5 h-5 text-green-500" />
            )}
            {lesson.title}
            {isCompleted && (
              <CheckCircle2 className="w-5 h-5 text-green-500" />
            )}
          </CardTitle>

          <div className="flex items-center gap-2 text-sm text-muted-foreground">
            <Clock className="w-4 h-4" />
            {lesson.duration}
          </div>
        </CardHeader>

        <CardContent className="space-y-6">
          {lesson.type === 'video' ? (
            <div className="space-y-4">
              <div className="aspect-video">
                <iframe
                  src={lesson.videoUrl}
                  className="w-full h-full rounded-lg"
                  allowFullScreen
                  title={lesson.title}
                />
              </div>
              {lesson.description && (
                <p className="text-muted-foreground">{lesson.description}</p>
              )}
            </div>
          ) : (
            <div className="space-y-4">
              <div className="prose prose-sm max-w-none dark:prose-invert">
                {lesson.content?.text.split('\n').map((paragraph, index) => {
                  if (paragraph.startsWith('# ')) {
                    return <h1 key={index} className="text-2xl font-bold mb-4">{paragraph.slice(2)}</h1>;
                  }
                  if (paragraph.startsWith('## ')) {
                    return <h2 key={index} className="text-xl font-semibold mb-3 mt-6">{paragraph.slice(3)}</h2>;
                  }
                  if (paragraph.startsWith('### ')) {
                    return <h3 key={index} className="text-lg font-medium mb-2 mt-4">{paragraph.slice(4)}</h3>;
                  }
                  if (paragraph.startsWith('- ')) {
                    return <li key={index} className="ml-6">{paragraph.slice(2)}</li>;
                  }
                  if (paragraph.startsWith('**') && paragraph.endsWith('**')) {
                    return <p key={index} className="font-semibold mb-2">{paragraph.slice(2, -2)}</p>;
                  }
                  if (paragraph.trim()) {
                    return <p key={index} className="mb-4">{paragraph}</p>;
                  }
                  return null;
                })}
              </div>

              {lesson.content?.images && lesson.content.images.length > 0 && (
                <div className="space-y-4">
                  {lesson.content.images.map((image, index) => (
                    <img
                      key={index}
                      src={image}
                      alt={`Imagem da aula ${index + 1}`}
                      className="w-full rounded-lg"
                    />
                  ))}
                </div>
              )}
            </div>
          )}

          <div className="flex flex-col sm:flex-row gap-3 pt-6 border-t">
            <div className="flex gap-3">
              <Button
                variant="outline"
                onClick={goToPrevLesson}
                disabled={!prevLesson}
              >
                <ArrowLeft className="w-4 h-4 mr-2" />
                {t('Previous')}
              </Button>

              <Button
                variant="outline"
                onClick={goToNextLesson}
                disabled={!nextLesson}
              >
                {t('Next')}
                <ArrowRight className="w-4 h-4 ml-2" />
              </Button>
            </div>

            <Button
              onClick={markAsCompleted}
              disabled={isCompleted}
              className="sm:ml-auto"
            >
              {isCompleted ? (
                <>
                  <CheckCircle2 className="w-4 h-4 mr-2" />
                  {t('Completed')}
                </>
              ) : (
                t('Mark as Completed')
              )}
            </Button>
          </div>
        </CardContent>
      </Card>
    </div>
  );
}

client/src/pages/meals.tsx.content:
import React, { useState } from "react";
import {
  Card,
  CardContent,
  CardHeader,
  CardTitle,
} from "../components/ui/card";
import { Button } from "../components/ui/button";
import { Badge } from "../components/ui/badge";
import {
  Dialog,
  DialogContent,
  DialogHeader,
  DialogTitle,
  DialogTrigger,
} from "../components/ui/dialog";
import {
  Utensils,
  Search,
  Filter,
  Eye,
  Plus,
  Clock,
  Target,
  Globe,
  Lock,
  ChefHat,
  Heart,
  Star,
} from "lucide-react";
import { Input } from "../components/ui/input";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../components/ui/select";
import { useTranslation } from "../hooks/useTranslation";
import { useApp } from "../contexts/AppContext";
import { MealLogDialog } from "../components/MealLogDialog";
import mealsData from "../data/meals.json";

interface Meal {
  id: string;
  name: {
    [key: string]: string;
  };
  calories: number;
  protein: number;
  carbs: number;
  fats: number;
  category: string;
  countries: string[];
  image?: string;
  ingredients?: {
    [key: string]: string[];
  };
  instructions?: {
    [key: string]: string[];
  };
}

const meals = mealsData as Meal[];

function Meals() {
  const { t, currentLanguage } = useTranslation();
  const { state, dispatch } = useApp();
  const [searchTerm, setSearchTerm] = useState("");
  const [categoryFilter, setCategoryFilter] = useState("all");
  const [countryFilter, setCountryFilter] = useState("all");
  const [showFavoritesOnly, setShowFavoritesOnly] = useState(false);
  const [selectedMeal, setSelectedMeal] = useState<Meal | null>(null);
  const [mealToLog, setMealToLog] = useState<Meal | null>(null);
  const [showLogDialog, setShowLogDialog] = useState(false);

  // Get favorite meals from user data
  const favoriteMeals = state.userData.favorites?.meals || [];

  // Extract unique countries from all meals
  const availableCountries = Array.from(
    new Set(meals.flatMap((meal) => meal.countries)),
  ).sort();

  // Toggle favorite meal
  const toggleFavorite = (mealId: string) => {
    dispatch({
      type: "TOGGLE_FAVORITE",
      payload: {
        type: "meals",
        id: mealId,
      },
    });
  };

  // Check if meal is favorite
  const isFavorite = (mealId: string) => {
    return favoriteMeals.includes(mealId);
  };

  const logMeal = (meal: Meal, date: string) => {
    // Update daily calories for the selected date
    const calories = state.userData.calories || [];
    const existing = calories.find(c => c.date === date);
    const updatedCalories = calories.filter(c => c.date !== date);
    const newTotal = (existing?.calories || 0) + meal.calories;

    updatedCalories.push({
      date: date,
      calories: newTotal,
    });

    dispatch({
      type: "SET_USER_DATA",
      payload: {
        ...state.userData,
        calories: updatedCalories,
      },
    });

    console.log(
      "Logging meal:",
      meal.name[currentLanguage] || meal.name["en-US"],
      "for date:",
      date,
    );
  };

  const handleLogMealClick = (meal: Meal) => {
    setMealToLog(meal);
    setShowLogDialog(true);
  };

  const handleConfirmLog = (date: string) => {
    if (mealToLog) {
      logMeal(mealToLog, date);
      setMealToLog(null);
    }
  };

  const getCategoryVariant = (category: string) => {
    switch (category) {
      case "breakfast":
        return "secondary";
      case "lunch":
        return "default";
      case "dinner":
        return "destructive";
      case "snack":
        return "outline";
      default:
        return "default";
    }
  };

  const filteredMeals = meals.filter((meal) => {
    const searchTermLower = searchTerm.toLowerCase();

    const mealName =
      meal.name[currentLanguage] ||
      meal.name["en-US"] ||
      Object.values(meal.name)[0] ||
      "";

    const mealNameLower = mealName.toLowerCase();

    // Check if search term matches meal name
    const matchesName = mealNameLower.includes(searchTermLower);

    // Check if search term matches any ingredient
    let matchesIngredients = false;
    if (meal.ingredients && searchTerm.trim()) {
      const ingredients = meal.ingredients[currentLanguage] || meal.ingredients["en-US"] || [];
      matchesIngredients = ingredients.some((ingredient: string) => 
        ingredient.toLowerCase().includes(searchTermLower)
      );
    }

    const matchesSearch = matchesName || matchesIngredients;
    const matchesCategory =
      categoryFilter === "all" || meal.category === categoryFilter;
    const matchesCountry =
      countryFilter === "all" || meal.countries.includes(countryFilter);
    const matchesFavorites = !showFavoritesOnly || isFavorite(meal.id);

    return matchesSearch && matchesCategory && matchesCountry && matchesFavorites;
  });

  return (
    <div className="container mx-auto py-6 px-4">
      <Card className="shadow-lg">
        <CardHeader className="bg-gradient-to-r from-green-50 to-blue-50 dark:from-green-900/20 dark:to-blue-900/20">
          <CardTitle className="text-3xl font-bold text-center mb-2">
            <Utensils className="w-8 h-8 inline mr-3" />
            {t("meals.title")}
          </CardTitle>
          <p className="text-center text-muted-foreground">
            {t("meals.subtitle")}
          </p>
        </CardHeader>
        <CardContent className="p-6">
          <div className="grid gap-6">
            {/* Search and Filter Controls */}
            <div className="flex flex-col md:flex-row gap-4">
              <div className="flex-1 relative">
                <Search className="w-5 h-5 text-gray-500 absolute left-3 top-1/2 transform -translate-y-1/2" />
                <Input
                  type="search"
                  placeholder={t("meals.search") || "Buscar por nome ou ingrediente..."}
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                  className="pl-10"
                />
              </div>

              <div className="flex items-center gap-2">
                <Filter className="w-5 h-5 text-gray-500" />
                <Select
                  value={categoryFilter}
                  onValueChange={setCategoryFilter}
                >
                  <SelectTrigger className="w-[200px]">
                    <SelectValue placeholder={t("meals.filter")} />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">
                      {t("meals.categories.all")}
                    </SelectItem>
                    <SelectItem value="breakfast">
                      {t("meals.categories.breakfast")}
                    </SelectItem>
                    <SelectItem value="lunch">
                      {t("meals.categories.lunch")}
                    </SelectItem>
                    <SelectItem value="dinner">
                      {t("meals.categories.dinner")}
                    </SelectItem>
                    <SelectItem value="snack">
                      {t("meals.categories.snack")}
                    </SelectItem>
                  </SelectContent>
                </Select>

                <Select value={countryFilter} onValueChange={setCountryFilter}>
                  <SelectTrigger className="w-[200px]">
                    <SelectValue
                      placeholder={t("meals.countryFilter")}
                    />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="all">
                      {t("meals.countries.all")}
                    </SelectItem>
                    {availableCountries.map((country) => (
                      <SelectItem key={country} value={country}>
                        {t(`meals.countries.${country}`)}
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>

                {/* Favorites Toggle */}
                <Button
                  variant={showFavoritesOnly ? "default" : "outline"}
                  size="sm"
                  onClick={() => setShowFavoritesOnly(!showFavoritesOnly)}
                  className="flex items-center gap-2"
                >
                  <Heart className={`w-4 h-4 ${showFavoritesOnly ? "fill-current" : ""}`} />
                  {t("meals.favoritesFilter")}
                </Button>
              </div>
            </div>

            {/* Results Count */}
            <div className="text-sm text-muted-foreground">
              {filteredMeals.length === 1 
                ? t("meals.resultsCount", { count: filteredMeals.length })
                : t("meals.resultsCountPlural", { count: filteredMeals.length })}
            </div>

            {/* Meals Grid */}
            <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
              {filteredMeals.map((meal) => {
                return (
                  <Card
