import { Progress as ProgressBar } from "../components/ui/progress";
import { Badge } from "../components/ui/badge";
import { Separator } from "../components/ui/separator";
import {
  Tabs,
  TabsContent,
  TabsList,
  TabsTrigger,
} from "../components/ui/tabs";
import {
  TrendingUp,
  Droplets,
  Scale,
  Calculator,
  BarChart3,
  RefreshCw,
  AlertCircle,
  CheckCircle,
  Target,
  Activity,
  Download,
  Upload,
  Utensils,
  User,
} from "lucide-react";
import { useTranslation } from "../hooks/useTranslation";
import { useApp } from "../contexts/AppContext";
import {
  LineChart,
  Line,
  XAxis,
  YAxis,
  CartesianGrid,
  Tooltip,
  ResponsiveContainer,
  BarChart,
  Bar,
} from "recharts";
import {
  WaterIntakeData,
  DailyCaloriesData,
  BodyCompositionData,
  calculateWaterIntake,
  getWaterIntakeMessage,
  calculateBMI,
  getBMIMessage,
  calculateDailyCalories,
  getCalorieMessage,
  calculateWaistToHipRatio,
  getWaistToHipMessage,
  calculateBodyFat,
  getBodyFatMessage,
  saveWaterIntake,
  loadWaterIntake,
  saveDailyCalories,
  loadDailyCalories,
  saveBodyComposition,
  loadBodyComposition,
  resetProgressData,
  isNewDay,
  getTodayDate,
} from "../utils/progressCalculations";
import { exportAllDataAsCSV } from "../utils/csvExport";
import { CSVImport } from "../components/CSVImport";
import { Dialog, DialogContent, DialogTrigger } from "../components/ui/dialog";
import { DailyWeightLogger } from "../components/DailyWeightLogger";
import { DailyMoodLogger } from "../components/DailyMoodLogger";
import { Journal } from "../components/Journal";

export default function Progress() {
  const { state } = useApp();
  const [showImportDialog, setShowImportDialog] = useState(false);
  const { t } = useTranslation();
  const { logWeight, logMood, logWater, logCalories } = useApp();
  const [timeFilter, setTimeFilter] = useState("1month");
  const [calorieTimeFilter, setCalorieTimeFilter] = useState("1month");
  const { userData } = state;
  // Helper function to filter data by time period
  const filterDataByTime = (data: any[], filter: string) => {
    if (!Array.isArray(data)) {
      console.warn("filterDataByTime received an invalid value:", data);
      return [];
    }

    const now = new Date();
    const filterDate = new Date();

    switch (filter) {
      case "1week":
        filterDate.setDate(now.getDate() - 7);
        break;
      case "1month":
        filterDate.setMonth(now.getMonth() - 1);
        break;
      case "3months":
        filterDate.setMonth(now.getMonth() - 3);
        break;
      case "6months":
        filterDate.setMonth(now.getMonth() - 6);
        break;
      default:
        return data;
    }

    return data.filter((item) => new Date(item.date) >= filterDate);
  };

  // Get user's current weight from saved data
  const getCurrentUserWeight = () => {
    if (!userData) return 70;

    const latestWeight =
      userData.weights && userData.weights.length > 0
        ? userData.weights[userData.weights.length - 1].weight
        : null;

    const profileWeight = userData.userProfile?.weight;

    return latestWeight || profileWeight || 70;
  };

  // Get user's saved profile data
  const getUserProfileData = () => {
    const profile = userData.userProfile || {};

    return {
      weight: getCurrentUserWeight(),
      height: profile.height || 175,
      age: profile.age || 30,
      sex: profile.gender || "male",
      activityLevel: profile.exerciseLevel || "moderate",
    };
  };

  // Water Intake State
  const [waterData, setWaterData] = useState<WaterIntakeData>({
    weight: getCurrentUserWeight(),
    activity: "moderate",
    goal: "maintain",
    recommendedMl: 2950,
    loggedMlToday: 0,
    lastUpdated: getTodayDate(),
  });
  const [waterInput, setWaterInput] = useState("");

  // Daily Calories State
