import React, { useState } from "react";
import { useLocation } from "wouter";
import { Button } from "../components/ui/button";
import { Card, CardContent } from "../components/ui/card";
import {
  Select,
  SelectContent,
  SelectItem,
  SelectTrigger,
  SelectValue,
} from "../components/ui/select";
import { useTranslation } from "../hooks/useTranslation";
import { useApp } from "../contexts/AppContext";

export default function Welcome() {
  const [, setLocation] = useLocation();
  const { t, changeLanguage } = useTranslation();
  const { updateUserProfile } = useApp();
  const [selectedLanguage, setSelectedLanguage] = useState<string>("");

  const languages = [
    { code: "en-NG", name: "English (Nigeria)", flag: "üá≥üá¨" },
    { code: "en-ZA", name: "English (South Africa)", flag: "üáøüá¶" },
    { code: "en-KE", name: "English (Kenya)", flag: "üá∞üá™" },
    { code: "en-GH", name: "English (Ghana)", flag: "üá¨üá≠" },
    { code: "fr-CI", name: "Fran√ßais (C√¥te d'Ivoire)", flag: "üá®üáÆ" },
  ];

  const handleLanguageSelect = (languageCode: string) => {
    setSelectedLanguage(languageCode);
  };

  const handleContinue = () => {
    if (selectedLanguage) {
      changeLanguage(selectedLanguage);
      updateUserProfile({ language: selectedLanguage as any });
      setLocation("/onboarding");
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-primary to-secondary flex items-center justify-center p-4">
      <div className="animate-fadeIn">
        <Card className="bg-white dark:bg-gray-800 rounded-3xl shadow-2xl p-8 max-w-md w-full text-center">
          <CardContent className="space-y-6">
            {/* App Logo */}
            <div className="w-32 h-32 mx-auto mb-6 bg-white rounded-full flex items-center justify-center shadow-lg">
              <img
                src="/logo.png"
                alt="NaijaReset Logo"
                className="w-24 h-24 object-contain rounded-full"
                onError={(e) => {
                  // Fallback to text logo if image fails to load
                  const target = e.target as HTMLImageElement;
                  target.style.display = "none";
                  const container = target.parentElement!;
                  container.innerHTML = `
                    <div class="w-24 h-24 bg-gradient-to-br from-accent to-primary rounded-full flex items-center justify-center">
                      <span class="text-white font-bold text-lg">NR</span>
                    </div>
                  `;
                }}
              />
            </div>

            {/* Welcome Text */}
            <div className="space-y-2">
              <h1 className="text-3xl font-poppins font-bold text-gray-800 dark:text-gray-100">
                My Reset
              </h1>
              <p className="text-gray-600 dark:text-gray-300">
                {t("welcome.subtitle")}
              </p>
            </div>

            {/* Language Selection */}
            <div className="space-y-4">
              <h2 className="text-xl font-poppins font-semibold text-gray-800 dark:text-gray-100">
                {t("welcome.chooseLanguage")}
              </h2>

              <div className="space-y-4">
                <Select
                  onValueChange={handleLanguageSelect}
                  value={selectedLanguage}
                >
                  <SelectTrigger className="w-full py-4 px-6 rounded-2xl border-2 border-gray-200 dark:border-gray-600">
                    <SelectValue placeholder="Select your language / Choisissez votre langue" />
                  </SelectTrigger>
                  <SelectContent>
                    {languages.map((language) => (
                      <SelectItem key={language.code} value={language.code}>
                        <span className="flex items-center space-x-3">
                          <span className="text-xl">{language.flag}</span>
                          <span>{language.name}</span>
                        </span>
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>

                <Button
                  onClick={handleContinue}
                  disabled={!selectedLanguage}
                  className="w-full bg-primary hover:bg-primary/90 disabled:bg-gray-300 disabled:cursor-not-allowed text-white py-4 px-6 rounded-2xl font-medium transition-all duration-200 transform hover:scale-105 disabled:hover:scale-100"
                >
                  {selectedLanguage
                    ? selectedLanguage.startsWith("fr")
                      ? "Continuer"
                      : "Continue"
                    : "Continue / Continuer"}
                </Button>
              </div>
            </div>
          </CardContent>
        </Card>
      </div>
    </div>
  );
}

client/src/types/index.ts.content:
export interface UserProfile {
  name: string;
  age: number;
  height: number;
  weight: number;
  targetWeight: number;
  gender: 'male' | 'female' | 'other';
  exerciseLevel: 'sedentary' | 'light' | 'moderate' | 'active' | 'very_active';
  diet: string[];
  language: 'en-US' | 'fr-FR' | 'es-ES';
}

export interface Challenge {
  id: string;
  name: {
    [key: string]: string; // e.g., "en-US": "Challenge Name", "fr-FR": "Nom du D√©fi"
  };
  description: {
    [key: string]: string; // e.g., "en-US": "Challenge Description", "fr-FR": "Description du D√©fi"
  };
  days: number;
  dailyTasks: DailyTask[];
  currentDay: number;
  completedDays: number[];
  isActive: boolean;
  startDate?: string;
  language?: string; // Store the language used when the challenge was created
  accessPlans?: number[];
}

export interface DailyTask {
  day: number;
  tasks: {
    [key: string]: string[]; // e.g., "en-US": ["Task 1", "Task 2"], "fr-FR": ["T√¢che 1", "T√¢che 2"]
  };
  completed: boolean[];
}

export interface Exercise {
  id: string;
  name: string;
  category: 'Light' | 'Moderate' | 'Advanced';
  duration?: string;
  reps?: string;
  rest: string;
  description?: string;
  isFavorite?: boolean;
  accessPlans?: number[];
}

export interface Meal {
  id: string;
  name: string | { [key: string]: string };
  description?: string | { [key: string]: string };
  category: 'breakfast' | 'lunch' | 'dinner' | 'snack';
  calories: number;
  protein: number;
  carbs: number;
  fats: number;
  cookingTime: string;
  servings: number;
  difficulty: 'easy' | 'medium' | 'hard';
  ingredients: string[] | { [key: string]: string[] };
  instructions: string[] | { [key: string]: string[] };
  image?: string;
  accessPlans?: number[];
}

export interface WeightLog {
  date: string;
  weight: number;
}

export interface MoodLog {
  date: string;
  mood: 'üòû' | 'üòê' | 'üôÇ' | 'üòä' | 'üòÑ';
}

export interface WaterLog {
  id?: string;
  date: string;
  liters: number;
  time: string;
  timestamp: string;
}

export interface CaloriesLog {
  date: string;
  calories: number;
}

export interface Measurements {
  date: string;
  waist?: number;
  hips?: number;
  chest?: number;
  arms?: number;
  thighs?: number;
}

export interface Badge {
  id: string;
  name: string;
  description: string;
  icon: string;
  isUnlocked: boolean;
  unlockedAt?: string;
}

export interface Course {
  id: string;
  title: {
    'en-NG': string;
    'fr-CI': string;
  };
  description: {
    'en-NG': string;
    'fr-CI': string;
  };
  image: string;
  accessPlans: number[];
  totalLessons: number;
  modules: CourseModule[];
}

export interface CourseModule {
  id: string;
  title: {
    'en-NG': string;
    'fr-CI': string;
  };
  lessons: Lesson[];
}

export interface Lesson {
  id: string;
  title: {
    'en-NG': string;
    'fr-CI': string;
  };
  type: 'video' | 'text';
  duration: string;
  videoUrl?: string;
  description?: {
    'en-NG': string;
    'fr-CI': string;
  };
  content?: {
    text: {
      'en-NG': string;
      'fr-CI': string;
    };
    images?: string[];
  };
}

export interface CourseProgress {
  courseId: string;
  completedLessons: string[];
  currentModule: string;
  currentLesson: string;
  startedAt: string;
  lastAccessedAt: string;
}

export interface LessonProgress {
  lessonId: string;
  completed: boolean;
}

export interface UserData {
  userProfile: UserProfile;
  challenges: Record<string, Challenge>;
  weights: WeightLog[];
  moods: MoodLog[];
  measurements: Measurements[];
  waterLog: WaterLog[];
  caloriesLog: CaloriesLog[];
  courseProgress: CourseProgress[];
  lessonProgress: LessonProgress[];
  badges: Badge[];
  favorites: {
    exercises: string[];
    meals: string[];
    quotes: string[];
  };
  exerciseHistory: Array<{
    exerciseId: string;
    date: string;
    duration: number;
    completed: boolean;
  }>;
  mealHistory: Array<{
    mealId: string;
    date: string;
    mealType: 'breakfast' | 'lunch' | 'dinner' | 'snack';
  }>;
}

export interface AppState {
  userData: UserData;
  currentLanguage: 'en-US' | 'fr-FR' | 'es-ES';
  isDarkMode: boolean;
  isOnboarded: boolean;
  currentChallenge?: string;
}

export interface AppContextType {
  state: AppState;
  dispatch: React.Dispatch<AppAction>;
  toggleDarkMode: () => void;
  setLanguage: (language: 'en-US' | 'fr-FR' | 'es-ES') => void;
  setOnboarded: () => void;
  startChallenge: (challengeId: string) => void;
  completeChallenge: (challengeId: string, day: number) => void;
  restartChallenge: (challengeId: string) => void;
  updateUserProfile: (profileData: Partial<UserData>) => void;
  importCSVData: (csvData: any[]) => Promise<{ success: boolean; imported: number; skipped: number; message?: string }>;
}

export type AppAction =
  | { type: 'TOGGLE_DARK_MODE' }
  | { type: 'SET_LANGUAGE'; payload: 'en-NG' | 'fr-CI' }
  | { type: 'SET_ONBOARDED' }
  | { type: 'SET_USER_DATA'; payload: UserData }
  | { type: 'START_CHALLENGE'; payload: string }
  | { type: 'COMPLETE_CHALLENGE'; payload: { challengeId: string; day: number } }
  | { type: 'RESTART_CHALLENGE'; payload: string }
  | { type: 'UPDATE_USER_PROFILE'; payload: Partial<UserData> }
  | { type: 'IMPORT_CSV_DATA'; payload: any[] };

